// eslint-disable-next-line
import React, { Component } from 'react';
import './search-panel.css'
export default class SearchPanel extends Component {
    state = {
        label: "",
        todoData: [
            this.createTododItem('Make awesome app'),
            this.createTododItem('Kiss Mila!!!'),
            this.createTododItem('Have a lunch')
        ]
    };
    onLabelChange = (e) => {
        this.setState({
            label: e.target.value //here I can add build function to change input 
        })
    }
    onSubmit = (e) => {
        this.setState(({ todoData }) => {
            const newArray = [todoData.filter({label:e.target.value})
            ];
            return {
                todoData: newArray
            }

        });
    }


    render() {
        return (
            <form onSubmit={this.onSubmit}>
                <input type='text'
                    className='form-control search-input'
                    onChange={this.onLabelChange}
                    //style in component
                    placeholder="Type your text here, please"
                    value={this.state.label}/>
            </form>)
    }
}


