import React, { Component } from 'react';
import AppHeader from '../app-header/'
import ToDoList from '../todo-list/'
import SearchPanel from '../search-panel/'
import ItemStatusFilter from '../item-status-filter/'
import AddComponent from '../add-component'
import './app.css';
export default class App extends Component {
    maxId = 100;
    state = {
        todoData: [
            this.createTododItem('Make awesome app'),
            this.createTododItem('Kiss Mila!!!'),
            this.createTododItem('Have a lunch')
        ]
    };
    createTododItem(label) {
        return {
            label,
            important: false,
            done: false,
            id: this.maxId++
        }
    }

    deleteItem = (id) => {
        this.setState(({ todoData }) => {
            const idx = todoData.findIndex((el) => el.id === id);
            const newArray = [...todoData.slice(0, idx),
            ...todoData.slice(idx + 1)
            ];
            return {
                todoData: newArray
            }

        });
    }


    AddItem = (text) => {
        const newItem = this.createTododItem(text);
        this.setState(({ todoData }) => {
            const newArr = [
                ...todoData,
                newItem
            ]
            return {
                todoData: newArr
            }
        })


    }
    onToggleImportant = (id) => {
        console.log('Toggle Important', id)
    };

    onToggleDone = (id) => {
        this.setState(({ todoData }) => {
            const idx = todoData.findIndex((el) => el.id === id);
            //1) обновляем объект
            const oldItem = todoData[idx];
            const newItem = { ...oldItem, done: !oldItem.done };
            //2) создаем новый объект
            const newArray = [...todoData.slice(0, idx),
                newItem,
            ...todoData.slice(idx + 1)
            ];
            return{
                todoData:newArray
            }
        })
    };


    render() {

        const { todoData} = this.state;
        const doneCount = todoData.filter((el)=>el.done).length;
        const todoCount =todoData.length - doneCount;
        return (<div className='todo-app' >

            <AppHeader todo={todoCount} done={doneCount} />
            <div className='top-panel d-flex'>
                <SearchPanel />
                <ItemStatusFilter />
            </div>
            <ToDoList todos={this.state.todoData}
                onDeleted={this.deleteItem}
                onToggleImportant={this.onToggleImportant}
                onToggleDone={this.onToggleDone} />
            <AddComponent AddItem={this.AddItem} />

        </div>)// сдесь мы указываем источник для свойства todos
    };
}
