import React, { Component } from 'react';
import './todo-list-item.css'
export default class TodoListItem extends Component {
    state = {
        done: false,
        important:false
         
    }; 
    onLabelClick = () => {// этот синтаксис позволяет менять текущее состояние компонента на противоположное
        this.setState(({done})=>{
            return{
                done: !done
            };
        });
    };

    onMarkImportant = () => {
        this.setState(({important})=>{
            return {
                important: !important
            };
        });
    };


    render() {
        const { label, onDeleted } = this.props
        // instead of label 
        //could be props
        const { done, important } = this.state;

        let classNames = 'todo-list-item';
        if (done) {
            classNames += ' done';
        }
        if(important){
            classNames+=' important';
        }

        const listStyle = {
            color: important ? 'steelblue' : 'black',
            fontWeight: important ? 'bold' : 'normal'
        };
        
        return (// для каждого элемента прикреплена функция onClick, 
            //меняющая состояние на противоположное она внутри каждого элемента(в теге) 
            <span className={classNames}>
            <span className='todo-list-item'>
                <span
                    className='todo-list-item-label'
                    style={listStyle}
                    onClick={this.onLabelClick}>
                    {label}
                </span>
                <button type='button'
                    className='btn btn-outline-success btn-sm float-right'
                    onClick={this.onMarkImportant}>
                    <i className='fa fa-exclamation' />
                </button>
                <button type='button'
                    className='btn btn-outline-danger btn-sm float-right'
                    onClick={onDeleted}>
                    <i className="fa fa-trash-o" />

                </button>
            </span>
            </span>);// instead of that coulld be props.label
    };// but because of destructurization syntax we can use already properties
}
