import React, { Component } from 'react';
import AppHeader from '../app-header/'
import ToDoList from '../todo-list/'
import SearchPanel from '../search-panel/'
import ItemStatusFilter from '../item-status-filter/'
import './app.css';
export default class App extends Component {
    state = {
        todoData: [
            { label: 'Kiss Mila', important: true, id: 0 },
            { label: 'Make awesome App', important: true, id: 1 },
            { label: 'Drink coffee', important: false, id: 2 }
        ]
    };

        deleteItem = (id) => {
            this.setState(({ todoData }) => {
                const idx = todoData.findIndex((el) => el.id === id);
                const newArray = [...todoData.slice(1, idx), ...todoData.slice(idx, +1)];
                return {
                    todoData: newArray
                }
      
    });
        }
    
    
    render() {
        return (<div className='todo-app' >

            <AppHeader todo={1} done={3} />
            <div className='top-panel d-flex'>
                <SearchPanel />
                <ItemStatusFilter />
            </div>
            <ToDoList todos={this.state.todoData}
                onDeleted={this.deleteItem} />
        </div>)// сдесь мы указываем источник для свойства todos
    };
}
